import styled from "styled-components";

const TimelineSectionWrapper = styled.div`
  padding: 85px 0;
  background:#fff;

  .whitBox {
    background: 'rgb(33, 150, 243)', boxShadow: '0 10px 40px rgb(93 70 232 / 15%)', color: '#fff'
  }
  @media only screen and (max-width: 768px) {
    .wallet-left-content {
      .heading {
        font-size: 35px;
      }
    }
  }
  @media only screen and (max-width: 480px) {
    padding: 50px 0;
    .wallet-left-content {
      .heading {
        font-size: 30px;
      }
    }
    .wallet-info{
      .counter-item{
        padding: 0 15px;
        span{
          font-size: 24px;
          line-height: 35px;
        }
      }
    } 
  }
  @media only screen and (max-width: 320px) {
    .wallet-left-content {
      .heading {
        font-size: 25px;
        line-height: 35px;
      }
    }
  }
`;

export default TimelineSectionWrapper;
